sys_variable_value.value:
(function execute(inputs, outputs) {
// I'll be manually compiling this because...I don't trust the payload builder to do this properly.
// Format is:
/* [
  {
    "upsert": {
      "description": "Test 1",
      "deviceId": 1,
      "type": "Test Property 1",
      "value": "8080"
    }
  },
  {
    "upsert": {
      "description": "Test 1",
      "deviceId": 1,
      "lastSeen": "2020-03-08T12:34:45",
      "type": "Test Property 2",
      "value": "8080"
    }
  }
] */

  var upload_stack = []

  //Iterate through records and push all relevant values onto the stack
  while(inputs.records.next())
  {
    var this_entry = {}
    this_entry.deviceId = parseInt(inputs.records.getValue('armis_device_id'))
    this_entry.type = inputs.records.getValue('armis_property_type_displayed')
    this_entry.description = inputs.records.getValue('armis_property_description_not_displayed')
    this_entry.value = inputs.records.getValue('value')

    //This seems weird but it's required due to the structure above
    parent_entry = {}
    parent_entry.upsert = this_entry
    upload_stack.push(parent_entry)
  }

  outputs.json = JSON.stringify(upload_stack)
})(inputs, outputs);

