sys_variable_value.value:
(function execute(inputs, outputs) {

  //Armis requires a file to be attached, it will not accept raw text. So, this script simply takes the text output and generates an attachment to use.

  var gr = new GlideRecord("sys_attachment");
  var gsa = new GlideSysAttachment();
  var attachmentId = gsa.write(gr, "ArmisImportCache.csv", 'text/plain', inputs.csv_text);
  outputs.debug_notes = "Attachment ID: " + attachmentId + "(" + typeof attachmentId + ")"

  outputs.attachment = gsa
  outputs.attachmentid = attachmentId
})(inputs, outputs);

