sys_variable_value.value:
(function execute(inputs, outputs) {

  //random MAC code from:  https://stackoverflow.com/questions/24621721/how-would-one-generate-a-mac-address-in-javascript
  //also adapted it to quicky generate random strings instead of fiddling with Math.random() mins and maxes

  //iterate through num_dummy_computers
  for(var i=0;i<inputs.num_dummy_computers;i++)
  {
    var rec = new GlideRecord('cmdb_ci_computer')
    rec.name = "AI_TEST_COMPUTER_" + "XXXXXX".replace(/X/g, function() { return "0123456789ABCDEF".charAt(Math.floor(Math.random() * 16)) });
    rec.mac_address = "XX:XX:XX:XX:XX:XX".replace(/X/g, function() { return "0123456789ABCDEF".charAt(Math.floor(Math.random() * 16)) });
    rec.ip_address = "10.100.134." + i
    rec.os = "Windows 10 Enterprise"
    rec.u_users = "John Doe"
    rec.serial_number = "XXXXXXXX".replace(/X/g, function() { return "0123456789ABCDEF".charAt(Math.floor(Math.random() * 16)) });
    rec.discovery_source = "Armis Integration Test"
    rec.insert()
  }
})(inputs, outputs);

