sys_variable_value.value:
(function execute(inputs, outputs) {
 var body = JSON.parse(inputs.body)

 outputs.found_mac = false
 if (body.success = true)
 {
   if (body.data.count > 0)
   {
     outputs.found_mac = true
     outputs.id = body.data.data[0].id
     outputs.armis_data = JSON.stringify(body.data.data[0])
   }
 } 
})(inputs, outputs);

