sys_variable_value.value:
(function execute(inputs, outputs) {
  if (inputs.mac_address==1) { outputs.mac_address = "a8:6d:aa:06:e0:aa" } else { outputs.mac_address = inputs.mac_address }
  if (inputs.access_token==1) { outputs.access_token = "ba99483b1272dc58b53d7ace27f6c452f9f2ceec63da683f2ac0e2260f7ab60d" } else { outputs.access_token = inputs.access_token }
})(inputs, outputs);

