sys_variable_value.value:
(function execute(inputs, outputs) {
  var json_stack = []
  var json = JSON.parse(inputs.json_string)

  //Loop through all "data" entries, of the structure:
  /*{
  "data": {
    "count": 3,
    "next": null,
    "prev": null,
    "results": [
      {
        "accessSwitch": "N/A",
        "category": "Computer",
    etc.*/


  for(var i=0;i<json.data.results.length;i++)
  {
    json_stack.push(JSON.stringify(json.data.results[i]))
  }
  outputs.devices_json_stack = json_stack
  outputs.devices_count = json_stack.length

})(inputs, outputs);

