sys_variable_value.value:
(function execute(inputs, outputs) {
  //When encapsulating within a CSV, per RFC4180, double-quotes need to be escaped by a second double-quote.
  //We do this by splitting on " and then joining with "".
  var sanitized_value = inputs.value.split("\"").join("\"\"")

  var id = inputs.id
  if(id == 0) { id = "" } //We want to send a blank string if the ID is 0, because otherwise Armis tries to match the 0 to a device ID

  outputs.line = "" + id + "," + inputs.mac + "," + inputs.name + ",\"" + sanitized_value + "\"";
})(inputs, outputs);

