sys_variable_value.value:
(function execute(inputs, outputs) {

  var threshold = parseInt(inputs.threshold);
  var num_matches = inputs.computer_count + inputs.adapters_count

  outputs.eligible_for_linking = num_matches < threshold;
  outputs.total_count = num_matches;

  //We need to weight how close of a match this is to a "good" mac/adapter. We do this by giving it a weight according to some context factors.

  var weight = 0;

  //Weight factor #1: in the 10.0.0.0/8 subnet
  if (inputs.ip_address != null) { if (inputs.ip_address.substring(0,3) == "10.") { weight += 1000 } }

  //Weight factor #2: Local area connection
  if (inputs.network_adapter_name != null) { if (inputs.network_adapter_name == "Local Area Connection") { weight += 1000 } }

  //Weight factor #3: Fewest duplicates possible
  weight += -100 * num_matches

  outputs.weight = weight

})(inputs, outputs);

