sys_variable_value.value:
(function execute(inputs, outputs) {

  switch(inputs.status) {
    case 200:
      var json = JSON.parse(inputs.body)
      var test_str = JSON.stringify(JSON.parse(inputs.body))
      outputs.debug = test_str.substring(test_str.length - 20, test_str.length)
     // outputs.debug = inputs.body.substring(inputs.body.length - 3, inputs.body.length)
      //if the string is too long, the last few chars are "..."
      //if this occurs, flag "too_many_records", which will signal the parent flow to reduce the number of records requested by 1 and try again.
      if (test_str.substring(test_str.length - 3, test_str.length) == "...")
      {
        outputs.too_many_records = true

      }
      else
      {
        outputs.too_many_records = false
        outputs.count = json.data.count
        outputs.next = json.data.next
        outputs.total = json.data.total

        var data_stack_array = []

        if (json.data.count > 0)
        {
          var i = 1
          for (i = 0; i < json.data.count; i++)
          {
              data_stack_array[i] = JSON.stringify(json.data.data[i])
          }
          outputs.data_stack = data_stack_array
        }
      }
  }
})(inputs, outputs);

