sys_variable_value.value:
(function execute(inputs, outputs) {

  // Create the DateTime objects for the comparisons

  var cooldown_end = new GlideDateTime(inputs.last_updated)
  cooldown_end.addSeconds(inputs.cooldown_end_seconds)

  var grace_period = new GlideDateTime(inputs.last_updated)
  grace_period.addSeconds(inputs.grace_period_seconds)

  //Status: -1 for grace period, 0 for cooldown period, 1 for after cooldown.
  //This is similar to the .compareTo() method.
  //(Doing it this way because I can't figure out how to reference a choice in another flow in this action)

  var now = new GlideDateTime()
  if(now.compareTo(grace_period) == -1) { outputs.status = -1}
  else if (now.compareTo(cooldown_end) == 1) { outputs.status = 1 }
  else { outputs.status = 0 }

})(inputs, outputs);

