sys_variable_value.value:
(function execute(inputs, outputs) {
  var json_stack = []
  outputs.operation_sys_ids = [];

  while (inputs.records.next())
  {
    //Argument is stored in the form of {"property_name":"SNOW Serial #","property_value":"30C3VT2","device_id":"554348"}
    var args = JSON.parse(inputs.records.arguments)

    //Arguments are already in their correctly formatted upsert blocks per the API, just need to stack them.
    json_stack.push(args)

    //retrieve the operation sys ID, so that it can be closed later by the flow.
    outputs.operation_sys_ids.push(inputs.records.getValue("sys_id"));
  }

  //Collapse the json stack into a string
  outputs.json = JSON.stringify(json_stack)
  outputs.operation_count = outputs.operation_sys_ids.length
})(inputs, outputs);

