sys_variable_value.value:
(function execute(inputs, outputs) {
  var device = JSON.parse(inputs.device_json_string)

  if(device.accessSwitch != null) { outputs.access_switch = device.accessSwitch }
  if(device.id != null) { outputs.armis_device_id = device.id }
  if(device.riskLevel != null) { outputs.armis_risk_level = device.riskLevel }
  if(device.category != null) { outputs.category = device.category }
  if(device.firstSeen != null) { outputs.first_seen = device.firstSeen }
  if(device.ipAddress != null) { outputs.ip_address = device.ipAddress }
  if(device.lastSeen != null) { outputs.last_seen = device.lastSeen }
  if(device.macAddress != null) { outputs.mac_address = device.macAddress.toUpperCase() }
  if(device.manufacturer != null) { outputs.manufacturer = device.manufacturer }
  if(device.model != null) { outputs.model = device.model }
  if(device.name != null) { outputs.name = device.name }
  if(device.operatingSystem != null) { outputs.os = device.operatingSystem }
  if(device.operatingSystemVersion != null) { outputs.os_version = device.operatingSystemVersion }
  if(device.sensor != null) { if(device.sensor.name != null) { outputs.sensor_name = device.sensor.name } }
  if(device.sensor != null) { if(device.sensor.type != null) { outputs.sensor_type = device.sensor.type } }
  if(device.site != null) { if(device.site.name != null) { outputs.site_name = device.site.name } }
  if(device.site != null) { if(device.site.location != null) {  outputs.site_location = device.site.location } }
  if(device.type != null) { outputs.type = device.type }
  if(device.user != null) { outputs.user = device.user }
  if(device.visibility != null) { outputs.visibility = device.visibility }

  //Tags need to be parsed separately
  var tags = []

  if(device.tags.count > 0)
  {
    for(var i=0;i<device.tags.count;i++)
    {
      tags[i] = device.tags[i]
    }
  }
  outputs.tags = tags

})(inputs, outputs);

