sys_variable_value.value:
(function execute(inputs, outputs) {

  var macs = ""

//We have at least 1 record, definitely.
while(inputs.records.next())
{
  //Comma-delimit if not the first record
  if (macs != "") { macs += "," }
  macs += inputs.records.getValue('mac_address')
}

  outputs.mac_string = macs
})(inputs, outputs);

