sys_variable_value.value:
(function execute(inputs, outputs) {
  var json_stack = []
  outputs.operation_sys_ids = [];

  while (inputs.records.next())
  {
    //Argument is stored in the form of {"property_name":"SNOW Serial #","property_value":"30C3VT2","device_id":"554348"}
    var args = JSON.parse(inputs.records.arguments)

    //Expected JSON is of the form:
    /*   {
      "upsert": {
        "description": "Blocked due to malicious app",
        "deviceId": 1,
        "lastSeen": "2020-03-08T12:34:45",
        "type": "Blocked Port",
        "value": "8080"
      }
    }*/

    //Note: I can't find anything where "description" shows up, so I'm just making it the same as the name.

    var this_json = {}
    this_json.upsert = {}
    this_json.upsert.description = args.property_name
    this_json.upsert.deviceId = parseInt(args.device_id)
    this_json.upsert.type = args.property_name
    var val = args.property_value
    this_json.upsert.value = args.property_value

    //Push this object onto the json stack
    if(val != null)
    {
      json_stack.push(this_json)
    }

    //retrieve the operation sys ID, so that it can be closed later by the flow.
    outputs.operation_sys_ids.push(inputs.records.getValue("sys_id"));
  }

  //Collapse the json stack into a string
  outputs.json = JSON.stringify(json_stack)
  outputs.operation_count = outputs.operation_sys_ids.length
})(inputs, outputs);

