sys_variable_value.value:
(function execute(inputs, outputs) {
  var t = new GlideDateTime();
  var s = parseInt(inputs.delay_seconds)
  if (s == null) { s = 0 }
  outputs.wait_until_s = s + (t.getNumericValue()/1000);
  outputs.queued_at = t
})(inputs, outputs);

