sys_variable_value.value:
(function execute(inputs, outputs) {
  //Need to make sure this record hasn't been deleted since we stored the request.
  if ( inputs.count > 0 )
  {
    while(inputs.records.next())
    {
      //This is really just "get the first item and store it as the target"
      //it's weird because ServiceNow starts a stack of records before the first record, "while(records.next())" always retrieves the next (in this case the first) one
      outputs.record = inputs.records
      outputs.is_valid_target = true
    }
  }
  else
  {
    outputs.is_valid_target = false
  }
})(inputs, outputs);

