sys_variable_value.value:
(function execute(inputs, outputs) {
  //For each, start with the existing vale, overwrite to the new value if it exists.

  if (inputs.record_mac != null) { outputs.mac = inputs.record_mac }
  if (inputs.input_mac != null) { outputs.mac = inputs.input_mac }

  if (inputs.record_id != null) { outputs.id = inputs.record_id }
  if (inputs.input_mac != null) { outputs.id = inputs.input_id }

  if (inputs.record_body != null) { outputs.body = inputs.record_body }
  if (inputs.input_body != null) { outputs.body = inputs.input_body }

  //If the "sensor" is "API", then we can assume ServiceNow can be authoritative
  outputs.source = "Armis"

  if (inputs.input_body != null & inputs.input_body != "") {
	  var body = JSON.parse(inputs.input_body)
	  if (body.sensor.type == "API") { outputs.source = "ServiceNow" }
  }

  outputs.now = new GlideDateTime();


})(inputs, outputs);

