sys_variable_value.value:
(function execute(inputs, outputs) {

  //We've stored the JSON string expiration date, compare it to an equivalent UTC time
  outputs.expired = inputs.expiration_utcstr < JSON.stringify(new Date()).replace('"','');
  outputs.compare = JSON.stringify(new Date()).replace('"','');

})(inputs, outputs);

