function writeToAPILog(p)
{
  if (p.payload == null) { p.payload = "" }
  if (p.response_body == null) { p.response_body = "" }
  var q = {}
  q.table = 'x_fmcna_armis_inte_armis_rest_api_log'
  q.update_pairs = [['execution_id',p.id],['api_endpoint',p.endpoint],['payload',p.payload.substring(0,1999)],['response_code',p.response_code],
    ['response_body',p.response_body.substring(0,1999)],['query_submitted_at',p.start_time],['query_response_time_ms',p.time_delta_ms]]
  return insertOrUpdate(q)
}


(function execute(inputs, outputs) {
  var execution_id = generateID()

  var endpoint = ''
  switch(inputs.record.type)
  {
    case 'ARP': endpoint = 'post_arp';break
    case 'CSV': endpoint = 'post_devices_csv';break
  }

  //Document the result, no matter what, to the API log.
  writeToAPILog({id: execution_id, endpoint: endpoint, payload: '' + inputs.record.content, response_code: inputs.status_code, response_body: inputs.status_message, start_time: inputs.startdatetime, time_delta_ms: (now() - inputs.start_time)})

  //If successful, document the success and set to complete.
  if(inputs.status_code == 200)
  {
    inputs.record.setValue('status','COMPLETE')
    inputs.record.update()

    writeToActionLog({id: execution_id, action_type: 'UP_UploadBatches(' + inputs.record.type.toLowerCase() + ')', total_time: (now() - inputs.start_time), api_time: (now() - inputs.start_time), heavy_processing_time: 0, report: "Uploaded " + inputs.record.type + " to Armis. Result: " + inputs.status_message, debug_string: '' + inputs.record.content})
  }
  else
  {
    //Otherwise, document the failure.
    writeToActionLog({id: execution_id, action_type: 'UP_UploadBatches(' + inputs.record.type.toLowerCase() + ')', total_time: (now() - inputs.start_time), api_time: (now() - inputs.start_time), heavy_processing_time: 0, report: "Failed to upload " + inputs.record.type + " to Armis. Result: (" + inputs.status_code + ") " + inputs.status_message, debug_string: '' + inputs.record.content}) 
  }
})(inputs, outputs);

