(function execute(inputs, outputs) {
  outputs.start_time = now()
  outputs.start_datetime = new GlideDateTime()
  outputs.access_token = getCurrentToken()

  switch(inputs.type)
  {
    case 'CSV':
      outputs.endpoint = 'devices/csv/';
      outputs.param_name = 'csv'
      break;
    case 'ARP':
      outputs.endpoint = 'arp/'
      outputs.param_name = 'arp'
      break
  }
