    writeToActionLog({id: execution_id, action_type: 'UP_UploadBatches(' + inputs.record.type.toLowerCase() + ')', total_time: (now() - inputs.start_time), api_time: (now() - inputs.start_time), heavy_processing_time: 0, report: "Failed to upload " + inputs.record.type + " to Armis. Result: (" + inputs.status_code + ") " + inputs.status_message, debug_string: '' + inputs.record.content}) 
  }
})(inputs, outputs);

