sys_variable_value.value:
(function execute(inputs, outputs) {
outputs.csv = "ip,mac\r\n";
outputs.operation_sys_ids = [];

while (inputs.records.next())
{
  //Grab the arguments field from each record, which should have the CSV line stored in it.
  var args = JSON.parse(inputs.records.getValue("arguments"))
  outputs.operation_sys_ids.push(inputs.records.getValue("sys_id"));

  //If for some reason we've populated invalid macs, we want to mark them off but not actually process them.
  if(args.ip == "0.0.0.0" || args.ip == "null" || args.ip == "" || args.ip == null) { continue }
  if(args.mac == "00:00:00:00:00:00" || args.mac == "null" || args.mac == "" || args.mac == null) { continue }
  outputs.csv += args.ip + "," + args.mac + "\r\n";
}

outputs.operation_count = outputs.operation_sys_ids.length
})(inputs, outputs);

