sys_variable_value.value:
(function execute(inputs, outputs) {

  //getNumericValue() returns time since epoch in ms
  var now = new GlideDateTime()
  now_numeric = now.getNumericValue()
  start_numeric = inputs.start_time.getNumericValue()
  outputs.time_delta_ms = now_numeric - start_numeric

  outputs.truncated_response = inputs.raw_response_body.substring(0,1900)

})(inputs, outputs);

