sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

  var i=0;
  var macs = [];
  var ips = [];
  var adapter_names = [];

  outputs.mac_found = false;

  if (inputs.inherent_mac_address != null & inputs.inherent_mac_addres != "")
  {
    macs[i] = inputs.inherent_mac_address
    ips[i] = inputs.inherent_ip_address
    i++;
  }

  //Iterate through all network adapters and get their macs/IPs
  while (true) {
    macs[i] = inputs.network_adapters.getValue("mac_address")
    ips[i] = inputs.network_adapters.getValue("ip_address")
    adapter_names[i] = inputs.network_adapters.getValue("name")
    i++;
    if (inputs.network_adapters.hasNext() == false) { break }
    inputs.network_adapters.next()
  }

  //Now iterate through all available macs and weight them according to IP and name.
  var top_weight = -1;
  var weights = [];
  var j=0;
  for (j=0; j < i; j++)
  {
    var weight = 0;
    if (ips[j] != null) { if (ips[j].substring(0,3) == "10.") { weight += 1000 } }
    if (adapter_names[j] != null) { if (adapter_names[j].substring(0,10) == "Local Area") { weight += 100 } }
    weights[j] = weight;

    if (weight > top_weight)
    {
      outputs.best_mac = macs[j];
      if (macs[j] != null) { outputs.mac_found = true }
      top_weight = weight;
    }
  }

  outputs.macs = macs;
  outputs.ips = ips;
  outputs.adapter_names = adapter_names;
  outputs.weights = weights
})(inputs, outputs);

