sys_variable_value.value:
(function execute(inputs, outputs) {

  //Extract the properties we want and store them in JSON format so they can be iterated properly
  var output_properties = []

  //Location
  var loc = {}
  loc.description = "SNOW Location"
  loc.value = inputs.location
  output_properties.push(JSON.stringify(loc))

  outputs.property_json = output_properties

})(inputs, outputs);

