sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  //Had to bring these in as strings because of typecast errors--going to fix the typecast errors by doing them in a script block.
  var start_num = parseInt(inputs.start_num)
  var num_records = parseInt(inputs.num_records)
  var total_records = start_num + num_records - 1
  outputs.total_records = total_records
})(inputs, outputs);

