sys_variable_value.value:
(function execute(inputs, outputs) {
  outputs.location = ""

  //Nested conditionals. Have to do this because doing this at the flow level locks up the instance for some reason.
  var computer = inputs.record.linked_snow_computer
  if (computer != null && computer != "" )
  {
    var location = computer.location
    if (location != null && location != "")
    {
      //Have to get rid of commas because for some reason they crash the entire ServiceNow instance. ....... 
      outputs.location = JSON.stringify(location.name.split(",").join(""))
    }
  }
})(inputs, outputs);

