sys_variable_value.value:
/*

This script attempts to link an unlinked SNOW record to any Armis record that hasn't linked yet, or vice versa. It does not make any API calls--it only checks the existing tables.

If a link is successful, it will be recorded under "Linked SNOW Computer" under the Armis 

NEW RULES (per Jason Stanford):


SNOW to Armis:
1. If the a NAME and MODEL match is found, and at least one SNOW MAC matches the Armis device's MAC, it's a golden match.
2. Assess all of the MACs associated with a device.
  a. Discard any MACs that appear in Network Adapters a number of times greater than the vmac_threshold
  b. Weight the MACs:
    i. Gets 1000 points if the IP associated is a 10.x.x.x
    ii. Gets 900 points if the adapter's name starts with "Local Area"
    iii. Loses 10 points for each time the MAC shows up in the network devices table.

Armis to SNOW:
1. If the a NAME and MODEL match is found, and at least one SNOW MAC matches the Armis device's MAC, it's a golden match.
(No additional attempts are made to match against the computers table.)

*/

//FUNCTION LIBRARY (v0.3) -- THESE FUNCTIONS HAVE BEEN MINIFIED.
//FOR FULL DOCUMENTAITON AND A CURRENT COPY, SEE THE ACTION "(vX.X) FUNCTION_LIBRARY"
function now(){return(new GlideDateTime).getNumericValue()}function includes(e,i){for(var t=0;t<e.length;t++)if(e[t]==i)return!0;return!1}function digest(e){for(var i=5381,t=e.length;t;)i=33*i^e.charCodeAt(--t);return i>>>0}function generateID(){var e="";arr="0123456789abcdefghijklmnopqrstuvwxyz";for(var i=16;i>0;i--)e+=arr[Math.floor(Math.random()*arr.length)];return e}function insertOrUpdate(e){var i={},t=now();null==e.unique_pairs&&(e.unique_pairs=[]);try{var a=new GlideRecord(e.table);if(0!=e.unique_pairs.length){for(var r=0;r<e.unique_pairs.length;r++)a.addQuery(e.unique_pairs[r][0],e.unique_pairs[r][1]);a.query()}if(0==a.getRowCount()){a.initialize();for(r=0;r<e.unique_pairs.length;r++)a[e.unique_pairs[r][0]]=e.unique_pairs[r][1];for(r=0;r<e.update_pairs.length;r++)a[e.update_pairs[r][0]]=e.update_pairs[r][1];a.insert(),i.operation="insert"}else{for(r=0;r<e.update_pairs.length;r++)a[e.update_pairs[r][0]]=e.update_pairs[r][1];a.updateMultiple(),i.operation="update"}}catch(e){i.operation="error: "+e}return i.time_delta=now()-t,i}function writeToAPILog(e){null==e.payload&&(e.payload=""),null==e.response_body&&(e.response_body="");var i={table:"x_fmcna_armis_inte_armis_rest_api_log"};return i.update_pairs=[["execution_id",e.id],["api_endpoint",e.endpoint],["payload",e.payload.substring(0,1999)],["response_code",e.response_code],["response_body",e.response_body.substring(0,1999)],["query_submitted_at",e.start_time],["query_response_time_ms",e.time_delta_ms]],insertOrUpdate(i)}function writeToActionLog(e){var i={table:"x_fmcna_armis_inte_armis_action_log"};return i.unique_pairs=[["execution_id",e.id]],i.update_pairs=[["action_type",e.action_type],["api_time",e.api_time],["heavy_processing_time",e.heavy_processing_time],["total_time",e.total_time],["report",e.report.substring(0,199)],["debug_string",e.debug_string.substring(0,1999)]],insertOrUpdate(i)}function getConfigValue(e){var i=new GlideRecord("x_fmcna_armis_inte_armis_integration_configuration");return i.addQuery("key",e),i.query(),0==i.getRowCount()?"":(i.next(),i.getValue("value"))}function getCacheValue(e,i){var t=new GlideRecord("x_fmcna_armis_inte_armis_integration_cache");if(t.addQuery("key",e),t.query(),0==t.getRowCount())return null;switch(t.next(),i){case"str":return t.getValue("value_string");case"int":return t.getValue("value_int");case"datetime":return t.getValue("value_datetime")}}function getCurrentToken(){return getCacheValue("token","str")}function setCacheValue(e,i,t){var a={table:"x_fmcna_armis_inte_armis_integration_cache"};switch(a.unique_pairs=[["key",e]],t){case"str":a.update_pairs=[["value_string",i]];break;case"int":a.update_pairs=[["value_int",i]];break;case"datetime":a.update_pairs=[["value_datetime",i]]}return insertOrUpdate(a)}function deleteCacheValue(e){var i=new GlideRecord("x_fmcna_armis_inte_armis_integration_cache");i.addQuery("key",e),i.query(),i.deleteMultiple()}function updateArmisDevice(e,i){var t=(new GlideDateTime).getNumericValue(),a={},r=new GlideRecord("x_fmcna_armis_inte_armis_device_table"),n=digest(JSON.stringify(e));a.digest=n,r.addQuery("armis_device_id",e.id),r.query();var s=0==r.getRowCount();s?r.initialize():r.next(),a.is_insert=s;var u,_=!0;if(s||r.getValue("digest")!=n||(_=!1),a.perform=_,a.digest_existing=r.getValue("digest"),_){if(r.access_switch=e.accessSwitch,r.armis_device_id=e.id,r.armis_risk_level=e.riskLevel,r.category=e.category,r.first_seen=new GlideDateTime(e.firstSeen),r.ip_address=e.ipAddress,r.last_seen=new GlideDateTime(e.lastSeen),r.mac_address=e.macAddress,r.manufacturer=e.manufacturer,r.model=e.model,r.name=e.name,r.os=e.operatingSystem,r.os_version=e.operatingSystemVersion,r.sensor_name=e.sensor.name,r.sensor_type=e.sensor.type,r.site=e.site.name,r.site_location=e.site.location,r.armis_tags=JSON.stringify(e.tags),r.visibility=e.visibility,r.digest=n,s?(u=r.insert(),a.action="insert"):(u=r.update(),a.action="update"),a.record=u,i){null==e.tags&&(e.tags=[]);var o=new GlideRecord("x_fmcna_armis_inte_armis_device_tags");o.addQuery("armis_device",u),o.query();for(var d=[];o.next();){var l=o.getValue("armis_tag");includes(d,o.getValue("armis_tag"))?d.push(l):o.deleteRecord()}for(var c=0;c<e.tags.length;c++)if(!includes(d,e.tags[c])){var p=new GlideRecord("x_fmcna_armis_inte_armis_device_tags");p.initialize(),p.armis_device=u,p.armis_tag=e.tags[c],p.insert()}}}else a.action="no_update_needed";return a.time_delta=(new GlideDateTime).getNumericValue()-t,a}

(function execute(inputs, outputs) {
/*SNOW to Armis:
1. If the a NAME and MODEL match is found, and at least one SNOW MAC matches the Armis device's MAC, it's a golden match.
2. Assess all of the MACs associated with a device.
  a. Discard any MACs that appear in Network Adapters a number of times greater than the vmac_threshold
  b. Weight the MACs:
    i. Gets 1000 points if the IP associated is a 10.x.x.x
    ii. Gets 900 points if the adapter's name starts with "Local Area"
    iii. Loses 10 points for each time the MAC shows up in the network devices table.

Armis to SNOW:
1. If the a NAME and MODEL match is found, and at least one SNOW MAC matches the Armis device's MAC, it's a golden match.
(No additional attempts are made to match against the computers table.)*/

  //Init
  var returns = {}
  var debug = []
  var start_time = now()

  switch (type)
  {
    case 'armis_to_snow' :
      //Find name and model match in the computer table.
      //if either the name or the model are null, we can't match.
      if (inputs.armis_record.name == null || inputs.armis_record.name == '' || inputs.armis_record.
      q = new GlideRecord()

      break;
    case 'snow_to_armis' :
      break
  }



})(inputs, outputs);

