sys_variable_value.value:
//GENERAL FUNCTION LIBRARY v0.8 MINIFIED
//SEE FUNCTION_LIBRARY ACTION FOR DOCUMENTATION AND FULL VERSION
function now(){return(new GlideDateTime).getNumericValue()}function includes(e,t){for(var a=0;a<e.length;a++)if(e[a]==t)return!0;return!1}function digest(e){for(var t=5381,a=e.length;a;)t=33*t^e.charCodeAt(--a);return t>>>0}function isValidIPAddress(e){return!!RegExp("^([01]?[0-9]?[0-9]|2[0-4][0-9]|25[0-5])\\.([01]?[0-9]?[0-9]|2[0-4][0-9]|25[0-5])\\.([01]?[0-9]?[0-9]|2[0-4][0-9]|25[0-5])\\.([01]?[0-9]?[0-9]|2[0-4][0-9]|25[0-5])$").test(e)||!!RegExp("^((?:[0-9A-Fa-f]{1,4}))((?::[0-9A-Fa-f]{1,4}))*::((?:[0-9A-Fa-f]{1,4}))((?::[0-9A-Fa-f]{1,4}))*|((?:[0-9A-Fa-f]{1,4}))((?::[0-9A-Fa-f]{1,4})){7}$").test(e)}function isValidMACAddress(e){return RegExp("^([0-9A-Fa-f]{2}[:-]){5}([0-9A-Fa-f]{2})|([0-9a-fA-F]{4}\\.[0-9a-fA-F]{4}\\.[0-9a-fA-F]{4})$").test(e)}function generateID(){var e="";arr="0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ";for(var t=6;t>0;t--)e+=arr[Math.floor(Math.random()*arr.length)];return e}function insertOrUpdate(e){var t={},a=now();null==e.unique_pairs&&(e.unique_pairs=[]);try{var r=new GlideRecord(e.table);if(0!=e.unique_pairs.length){for(var i=0;i<e.unique_pairs.length;i++)r.addQuery(e.unique_pairs[i][0],e.unique_pairs[i][1]);r.query()}if(0==r.getRowCount()){r.initialize();for(i=0;i<e.unique_pairs.length;i++)r[e.unique_pairs[i][0]]=e.unique_pairs[i][1];for(i=0;i<e.update_pairs.length;i++)r[e.update_pairs[i][0]]=e.update_pairs[i][1];r.insert(),t.operation="insert"}else{for(i=0;i<e.update_pairs.length;i++)r[e.update_pairs[i][0]]=e.update_pairs[i][1];r.updateMultiple(),t.operation="update"}}catch(e){t.operation="error: "+e}return t.time_delta=now()-a,t}function writeToActionLog(e){var t={table:"x_fmcna_armis_inte_armis_action_log"};return t.unique_pairs=[["execution_id",e.id]],t.update_pairs=[["action_type",e.action_type],["api_time",e.api_time],["heavy_processing_time",e.heavy_processing_time],["total_time",e.total_time],["report",e.report.substring(0,199)],["debug_string",e.debug_string.substring(0,1999)]],insertOrUpdate(t)}function getConfigValue(e){var t=new GlideRecord("x_fmcna_armis_inte_armis_integration_configuration");return t.addQuery("key",e),t.query(),0==t.getRowCount()?"":(t.next(),t.getValue("value"))}function getCacheValue(e,t){var a=new GlideRecord("x_fmcna_armis_inte_armis_integration_cache");if(a.addQuery("key",e),a.query(),0==a.getRowCount())return null;switch(a.next(),t){case"str":return a.getValue("value_string");case"int":return a.getValue("value_int");case"datetime":return a.getValue("value_datetime")}}function getCurrentToken(){return getCacheValue("token","str")}function setCacheValue(e,t,a){var r={table:"x_fmcna_armis_inte_armis_integration_cache"};switch(r.unique_pairs=[["key",e]],a){case"str":r.update_pairs=[["value_string",t]];break;case"int":r.update_pairs=[["value_int",t]];break;case"datetime":r.update_pairs=[["value_datetime",t]]}return insertOrUpdate(r)}function deleteCacheValue(e){var t=new GlideRecord("x_fmcna_armis_inte_armis_integration_cache");t.addQuery("key",e),t.query(),t.deleteMultiple()}

// INSERT OR UPDATE
// No "insert or update" function exists natively, so rather than rewriting the entire structure in every implementation, I am abstracting it here.
// INPUT (hash): 
//     .table : name of the target table
//     .unique_pairs : array of key-value pairs to check uniqueness on. If these pairs are found, will update, if not, will insert.
//     .update_pairs : array of key-value pairs that will be changed in the update or inserted along with the unique pairs.
// Note: this operation will run MUCH MUCH FASTER if the unique_pairs together form the primary key of the table.
// Example:
//          r = {}
//          r.table = 'x_fmcna_armis_inte_armis_op_arp_table'
//          r.unique_pairs = [ ['id','test_id'] , ['mac', 'test_mac' ] ]  -- note if this array is null or empty goes straight to an insert of the updated pairs
//          r.update_pairs = [ ['status', 'COMPLETE'] ]
//          insertOrUpdate(r)
// RETURN (hash):
//     .operation - string 'insert' or string 'update'. (returns 'error: [message]' if anything goes wrong.)
//     .time_delta - amount of time in milliseconds it took for the operation to run

function rInt(max) { return Math.floor(Math.random() * max) } // [0..max-1]
function randomSysIDStart() { return "X".replace(/X/g, function() { return "0123456789abcdef".charAt(Math.floor(Math.random() * 16)) })}
function randomMAC() { return "XX:XX:XX:XX:XX:XX".replace(/X/g, function() { return "0123456789ABCDEF".charAt(Math.floor(Math.random() * 16)) })}
function randomStatus() { return (['Operational','Non-Operational','Retired'])[Math.floor(Math.random() * 3)] }

function randomReference(table,type)
{
  var q = new GlideRecord(table)
  q.addQuery('sys_id','STARTSWITH', randomSysIDStart())
  q.query()

  var n = rInt(q.getRowCount()) % 100
  for(var i=0;i<n;i++) { q.next() }

  switch(type){
    case 'display': return q.getDisplayValue()
    case 'id': return q.sys_id
  }
}

function generateUpdatePairs(type)
{
  	var up = []
    up.push(['name','AI_TEST_COMPUTER_' + generateID().toUpperCase()])
    up.push(['ip_address','10.100.' + rInt(256) + '.' + rInt(256)])
    if(type=='new') { up.push(['mac_address',randomMAC()])}
    up.push(['serial_number',generateID()])
    up.push(['dns_domain',generateID()])
    up.push(['discovery_source',generateID()])
    up.push(['operational_status',randomStatus()])
    up.push(['support_group',randomReference('sys_user_group','id')])
    up.push(['location',randomReference('cmn_location','id')])
    up.push(['assigned_to',randomReference('sys_user','id')])
    up.push(['short_description',generateID()])
    up.push(['comments',generateID()])
    up.push(['first_discovered',generateID()])
    up.push(['sys_created_by',generateID()])
    up.push(['default_gateway',generateID()])
    up.push(['fqdn',generateID()])
    up.push(['u_last_backup',generateID()])
    up.push(['last_discovered',new GlideDateTime()])
    up.push(['u_retirement_date',new GlideDateTime()])
    up.push(['u_regulators',generateID()])

    return up
}

function assignDummySoftwares(snow_computer,software_ids)
{
  for(var i=0;i<software_ids.length;i++)
  {
    var sw_id = software_ids[i]
    var r = {}
    r.table = 'cmdb_software_instance'
    r.unique_pairs = [['installed_on',snow_computer.sys_id],['software',sw_id]]
    r.update_pairs = [['uninstall_string'],now()]
    insertOrUpdate(r)
  }
}


(function execute(inputs, outputs) {
  var debug = []
  var start_time = now()
  var min_num_computers = 100
  var min_num_softwares = 10
  var current_computer_count = 0
  var total_updates = 0
  var total_software_updates = 0
  var table_name = 'cmdb_ci_computer'
  var last_now = now()
  var software_ids = []
  var error_message = ''

  try
  {
    //Initialize softwares.
    var softwares = new GlideRecord('cmdb_ci_spkg')
    softwares.addQuery('name','STARTSWITH','AI_TEST_SOFTWARE_')
    softwares.query()
    debug.push("Dummy softwares: " + softwares.getRowCount() + "/" + min_num_softwares + " ( " + (now() - last_now) + "ms)");last_now=now()
    while(softwares.next()) { software_ids.push(softwares.sys_id) }
    debug.push("Software IDs: " + software_ids + " ( " + (now() - last_now) + "ms)");last_now=now()

    //Add any missing softwares for the next run
    for(var i=softwares.getRowCount();i<min_num_softwares;i++)
    {
      var insert_software = new GlideRecord('cmdb_ci_spkg')
      insert_software.initialize()
      insert_software.name = 'AI_TEST_SOFTWARE_' + generateID()
      insert_software.insert()
      debug.push("Inserted new dummy software." + " ( " + (now() - last_now) + "ms)");last_now=now()
    }

    //Spam updates until we're out of time.
    while((now()-start_time) < inputs.max_ms)
    {
      var q = new GlideRecord('cmdb_ci_computer')
      q.addQuery('name','STARTSWITH','AI_TEST_COMPUTER_')
      q.query()

      while(q.next() && (now()-start_time) < inputs.max_ms)
      {
        current_computer_count++
        var r = {}
        r.table = table_name
        r.unique_pairs = [['sys_id',q.sys_id]]
        r.update_pairs = generateUpdatePairs('update')
        total_updates += r.update_pairs.length
        insertOrUpdate(r)
        debug.push("Updated record: " + q.name + " ( " + (now() - last_now) + "ms)");last_now=now()

        assignDummySoftwares(q,software_ids)
        total_software_updates += software_ids.length
        debug.push("Updated dummy softwares. ( " + (now() - last_now) + "ms)");last_now=now()
      }

      for(var i=current_computer_count;i<min_num_computers;i++)
      {
        if(now()-start_time > inputs.max_ms) { break }
        var r = {}
        r.table = table_name
        r.update_pairs = generateUpdatePairs('new')
        total_updates += r.update_pairs.length
        insertOrUpdate(r)
        debug.push("Inserted new record: " + r.update_pairs[0][1] + " ( " + (now() - last_now) + "ms)");last_now=now()
      }
    }
  }
  catch(ex)
  {
    error_message = "[ERROR: " + ex + "] "
    debug.push(error_message)
  }

  debug.push("Total time: " + (now() - start_time))
  outputs.debug = debug.join('\n')

  var report_string = error_message + "Updated " + total_updates + " computer fields, " + total_software_updates + " softwares."
  writeToActionLog({id: generateID(), action_type: "STRESS_TEST", total_time: (now() - start_time), api_time: 0, heavy_processing_time: (now() - start_time), report: report_string, debug_string: debug.join('\n')})
})(inputs, outputs);

