sys_variable_value.value:
/* This script attempts to link:

1. Any Armis Device Table devices that have not yet attempted to link to a SNOW device
2. Populate the "Armis Link Reference" table with any SNOW computers that are not already on it.
3. Use this table to attempt a link on SNOW devices that have not yet attempted to link to an Armis device.

Once an Armis device attempts to link, the column "Novel Link Attempted" will be set to true. Once a SNOW computer attempts to link, it gets tagged with "Attempted Armis Link"

*/


//FUNCTION LIBRARY (v0.6) -- THESE FUNCTIONS HAVE BEEN MINIFIED.
//FOR FULL DOCUMENTAITON AND A CURRENT COPY, SEE THE ACTION "(vX.X) FUNCTION_LIBRARY"
function now(){return(new GlideDateTime).getNumericValue()}function includes(e,t){for(var r=0;r<e.length;r++)if(e[r]==t)return!0;return!1}function digest(e){for(var t=5381,r=e.length;r;)t=33*t^e.charCodeAt(--r);return t>>>0}function generateID(){var e="";arr="0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ";for(var t=6;t>0;t--)e+=arr[Math.floor(Math.random()*arr.length)];return e}function insertOrUpdate(e){var t={},r=now();null==e.unique_pairs&&(e.unique_pairs=[]);try{var a=new GlideRecord(e.table);if(0!=e.unique_pairs.length){for(var i=0;i<e.unique_pairs.length;i++)a.addQuery(e.unique_pairs[i][0],e.unique_pairs[i][1]);a.query()}if(0==a.getRowCount()){a.initialize();for(i=0;i<e.unique_pairs.length;i++)a[e.unique_pairs[i][0]]=e.unique_pairs[i][1];for(i=0;i<e.update_pairs.length;i++)a[e.update_pairs[i][0]]=e.update_pairs[i][1];a.insert(),t.operation="insert"}else{for(i=0;i<e.update_pairs.length;i++)a[e.update_pairs[i][0]]=e.update_pairs[i][1];a.updateMultiple(),t.operation="update"}}catch(e){t.operation="error: "+e}return t.time_delta=now()-r,t}function writeToAPILog(e){null==e.payload&&(e.payload=""),null==e.response_body&&(e.response_body="");var t={table:"x_fmcna_armis_inte_armis_rest_api_log"};return t.update_pairs=[["execution_id",e.id],["api_endpoint",e.endpoint],["payload",e.payload.substring(0,1999)],["response_code",e.response_code],["response_body",e.response_body.substring(0,1999)],["query_submitted_at",e.start_time],["query_response_time_ms",e.time_delta_ms]],insertOrUpdate(t)}function queueProperty(e){if(null==e.name||""==e.name)return{operation:"error: empty name",time_delta:0};if(null==e.value||""==e.value)return{operation:"error: empty value",time_delta:0};var t={table:"x_fmcna_armis_inte_armis_op_property_upload"};return t.update_pairs=[["armis_device_id",e.armis_id],["property_name",e.name],["property_value",e.value],["execution_id",e.execution_id],["status","PENDING"]],insertOrUpdate(t)}function queueARP(e){if(null==e.mac||""==e.mac)return{operation:"error: empty mac",time_delta:0};if(null==e.ip||""==e.ip)return{operation:"error: empty ip",time_delta:0};var t={table:"x_fmcna_armis_inte_armis_op_arp_table"};return t.update_pairs=[["execution_id",e.execution_id],["mac",e.mac],["ip",e.ip],["status","PENDING"]],insertOrUpdate(t)}function queueKeyAttribute(e){if(!(null!=e.armis_id&&""!=e.armis_id||null!=e.mac&&""!=e.mac))return{operation:"error: must have either a MAC or an Armis ID",time_delta:0};if(allowed_attributes=["CATEGORY","IP","LAST_SEEN","MODEL","NAME","OS","OS_VERSION","TAG","TYPE","USER"],0==includes(allowed_attributes,e.attribute))return{operation:"error: attribute '"+e.attribute+"' is not valid.",time_delta:0};var t={table:"x_fmcna_armis_inte_armis_op_key_attribute"};return t.update_pairs=[["execution_id",e.execution_id],["armis_device_id",e.mac],["armis_mac",e.mac],["attribute",e.attribute],["value",e.value],["status","PENDING"]],insertOrUpdate(t)}function queueApplication(e){if(null==e.armis_id||""==e.armis_id)return{operation:"error: Armis ID cannot be empty",time_delta:0};if(null==e.name||""==e.name)return{operation:"error: Software name cannot be empty",time_delta:0};var t={table:"x_fmcna_armis_inte_armis_op_application_upload"};return t.update_pairs=[["execution_id",e.execution_id],["armis_device_id",e.mac],["application_name",e.name],["application_version",e.version],["status","PENDING"]],insertOrUpdate(t)}function writeToActionLog(e){var t={table:"x_fmcna_armis_inte_armis_action_log"};return t.unique_pairs=[["execution_id",e.id]],t.update_pairs=[["action_type",e.action_type],["api_time",e.api_time],["heavy_processing_time",e.heavy_processing_time],["total_time",e.total_time],["report",e.report.substring(0,199)],["debug_string",e.debug_string.substring(0,1999)]],insertOrUpdate(t)}function getConfigValue(e){var t=new GlideRecord("x_fmcna_armis_inte_armis_integration_configuration");return t.addQuery("key",e),t.query(),0==t.getRowCount()?"":(t.next(),t.getValue("value"))}function getCacheValue(e,t){var r=new GlideRecord("x_fmcna_armis_inte_armis_integration_cache");if(r.addQuery("key",e),r.query(),0==r.getRowCount())return null;switch(r.next(),t){case"str":return r.getValue("value_string");case"int":return r.getValue("value_int");case"datetime":return r.getValue("value_datetime")}}function getCurrentToken(){return getCacheValue("token","str")}function setCacheValue(e,t,r){var a={table:"x_fmcna_armis_inte_armis_integration_cache"};switch(a.unique_pairs=[["key",e]],r){case"str":a.update_pairs=[["value_string",t]];break;case"int":a.update_pairs=[["value_int",t]];break;case"datetime":a.update_pairs=[["value_datetime",t]]}return insertOrUpdate(a)}function deleteCacheValue(e){var t=new GlideRecord("x_fmcna_armis_inte_armis_integration_cache");t.addQuery("key",e),t.query(),t.deleteMultiple()}function getPendingRecordsEndSysID(e,t,r){null==r&&(r={}),null==r.force_field&&(r.force_field="status"),null==r.force_pending&&(r.force_pending="PENDING");var a=new GlideRecord(t);for(a.addQuery(r.force_field,r.force_pending),a.setLimit(e),a.orderBy("sys_id"),a.query();a.next();)if(0==a.hasNext())return a.getValue("sys_id")}function getNumPendingRecords(e,t,r,a){null==a&&(a={}),null==a.force_field&&(a.force_field="status"),null==a.force_pending&&(a.force_pending="PENDING");var i=new GlideRecord(t);return i.addQuery(a.force_field,a.force_pending),i.setLimit(e),i.orderBy("sys_id"),i.addQuery("sys_id","<=",r),i.query(),i}function executeStandardREST(e){try{var t=new GlideDateTime,r=new sn_ws.RESTMessageV2("Armis API",e.endpoint);r.setRequestHeader("Authorization",getCurrentToken()),r.setRequestHeader("accept","application/json"),r.setRequestHeader("content-type","application/json"),null==e.query_params&&(e.query_params=[]);for(var a=0;a<e.query_params.length;a++)r.setQueryParameter(e.query_params[a][0],e.query_params[a][1]);r.setRequestBody(e.payload);var i,n=now(),u={};return 1==e.async?i=r.executeAsync():(i=r.execute(),u.response_code=i.getStatusCode(),u.response_body=i.getBody()),u.api_time=now()-n,writeToAPILog({id:e.id,endpoint:e.endpoint,payload:e.payload,response_code:u.response_code,response_body:u.response_body,start_time:t,time_delta_ms:u.api_time}),u}catch(e){return(u={response_code:"xx"}).response_body="Failed to build query. "+e,u.api_time=0,u}}function queueAttachmentREST(e){var t=new GlideRecord("sys_attachment"),r=(new GlideSysAttachment).write(t,e.id+".csv","text/plain",e.payload),a=new GlideRecord("x_fmcna_armis_inte_armis_attachment_upload");a.initialize(),a.attachment=r,a.execution_id=e.id,a.status="PENDING",a.type=e.type,a.insert()}


// *********************************
// *** ACTION-SPECIFIC FUNCTIONS ***
// *********************************

//Gets all the MACs associated with a given SNOW computer. (The MAC field itself + all MACs on network adapters associated with the computer.)
function getMACs(snow_computer_query)
{
  var macs = []

  var smac = snow_computer_query.mac_address
  if (smac == null) { smac = '' } else { smac = ('' + smac).trim().toUpperCase() }
  if(smac != '' && !includes(macs,smac)) { macs.push(smac) }

  var na = new GlideRecord('cmdb_ci_network_adapter')
  na.addQuery('cmdb_ci',snow_computer_query.sys_id)
  na.query()

  while(na.next()) {
    var smac = na.mac_address
    if (smac == null) { smac = '' } else { smac = ('' + smac).trim().toUpperCase() }
    if(smac != '' && !includes(macs,smac)) { macs.push(smac) }  
  }

  return macs
}

//A golden match is one where a SNOW device is found that fits the following conditions:
// 1. Name matches
// 2. Model matches
// 3. At least one MAC associated with the SNOW computer is the Armis device's MAC.
function attemptGoldenMatch(armis_computer_query)
{
  var ret = {}
  ret.match = false
  //If the name is '', we can't golden match.
  if(armis_computer_query.name == '') { ret.debug = "Name is empty, unable to attempt golden match.";return ret }
  var debug = []
  debug.push("Attempting golden link for link Armis device '" + armis_computer_query.name + "'")


  //Model is tricky, so need to sanitize
  var armis_model = ''
  if( armis_computer_query.model != 'Unknown') { armis_model = armis_computer_query.model }

  //Golden match: Check for exact matches on the name and model.
  var snow_device = new GlideRecord('cmdb_ci_computer')
  snow_device.addQuery('sys_class_name','!=','u_cmdb_ci_computers_from_armis')
  snow_device.addQuery('name','=',armis_computer_query.name)
  snow_device.query()
  debug.push("--Matches found for name only: " + snow_device.getRowCount())

  while(snow_device.next())
  {
    debug.push("--Evaluating SNOW device: " + snow_device.name)
    var snow_model = ''
    if( snow_device.model_id != null ) { if( snow_device.model_id.name != null) { snow_model = snow_device.model_id.name } }

    //Golden match condition: model matches
    debug.push("--Comparing models: '" + snow_model + "' == '" + armis_model + "'")
    if(snow_model == armis_model)
    {
      //Golden match condition: at least one MAC matches.
      var macs = getMACs(snow_device)
      debug.push("--Evaluating MACs vs Armis MAC " + ('' + armis_computer_query.mac_address).toUpperCase() + " : [" + macs + "]")
      if(includes(macs,('' + armis_computer_query.mac_address).toUpperCase()))
      {
        debug.push("--Golden match found.")
        ret.matched_device_sys_id = '' + snow_device.sys_id
        ret.match = true
        break
      }
      else { debug.push("--Failed to find MAC in array.") }
    }
    else { debug.push("--Failed model match.") }
  }

  ret.debug = debug.join('\n')
  return ret
}

//A MAC match occurs when:
// 1. The Armis device's MAC is not disqualified due to being a virtual MAC (it does not appear more than X times in the CMDB)
// 2. The Armis device's MAC appears on at least one computer as an associated MAC
//The match is whichever computer where the MAC appears most frequently.
function attemptMACMatch(armis_computer_query)
{
  var ret = {}
  ret.match = false
  var debug = []
  debug.push("Attempting MAC link for Armis device '" + armis_computer_query.name + "'")

  var vmac_threshold = parseInt(getConfigValue('vmac_threshold'))
  debug.push("--VMAC Threshold: " + vmac_threshold)

  //Count number of time MAC occurs.
  var tables = ['cmdb_ci_network_adapter','cmdb_ci_computer']
  var mac_count = 0
  for (var j=0;j<tables.length;j++)
  {
    var q = new GlideAggregate(tables[j])
    q.addAggregate('COUNT')
    q.addQuery('mac_address','=',('' + armis_computer_query.mac_address).toUpperCase())
    q.addQuery('sys_class_name','!=','u_cmdb_ci_computers_from_armis')
    q.query()
    if(q.next()) { mac_count += parseInt(q.getAggregate('COUNT')) }
  }
  debug.push("--Occurrences of MAC '" + armis_computer_query.mac_address + "': " + mac_count)

  //Only continue if we are under the vmac threshold.
  if (mac_count < vmac_threshold)
  {
    //Start counting the computers by occurrence of the MAC.
    var counts = {}
    var debug_lookup = {}
    var top_count = 0
    var top_sys_id = ''
    var q = new GlideRecord('cmdb_ci_computer')
    q.addQuery('mac_address','=',armis_computer_query.mac_address)
    q.addQuery('sys_class_name','!=','u_cmdb_ci_computers_from_armis')
    q.query()
    while(q.next())
    {
      if(counts[q.sys_id] == null) { counts[q.sys_id] = 0; debug_lookup[q.sys_id] = q.name }
      counts[q.sys_id]++
      if (counts[q.sys_id] > top_count) { top_count = counts[q.sys_id]; top_sys_id = q.sys_id }
    }

    //Count network adapters by occurrence of the MAC.
    q = new GlideRecord('cmdb_ci_network_adapter')
    q.addQuery('mac_address','=',armis_computer_query.mac_address)
    q.addQuery('sys_class_name','!=','u_cmdb_ci_computers_from_armis')
    q.query()
    while(q.next())
    {
      if(q.cmdb_ci == null) { continue } //If for some reason there's no attached CI, skip this entry.
      if(counts[q.cmdb_ci.sys_id] == null) { counts[q.cmdb_ci.sys_id] = 0; debug_lookup[q.cmdb_ci.sys_id] = q.cmdb_ci.name }
      counts[q.cmdb_ci.sys_id]++
      if (counts[q.cmdb_ci.sys_id] > top_count) { top_count = counts[q.cmdb_ci.sys_id]; top_sys_id = q.cmdb_ci.sys_id }
    }
    debug.push("--Counts: " + JSON.stringify(counts))
    debug.push("--Top count: " + top_sys_id + " (" + debug_lookup[top_sys_id] + ")")

    if (top_sys_id != '')
    {
      debug.push("--MAC Match found.")
      ret.match = true
      ret.matched_device_sys_id = '' + top_sys_id
    }
  }

  ret.debug = debug.join("\n")
  return ret
}

// **********************
// *** IMPLEMENTATION ***
// **********************

(function execute(inputs, outputs) {


  var start_time = now()
  var report_string = ''
  var api_total = 0
  var heavy_total = 0
  var debug = ['BEGIN LINK_AttemptAllArmisLinks']

  var armis_attempts_count = 0
  var armis_successes_count = 0

  //Loop until out of records or out of time.
  var end_loop_early = false
  var loop_counter = 0
  var max_processing_seconds = parseInt(inputs.max_processing_seconds)
  if(isNaN(max_processing_seconds)) { max_processing_seconds = 240 }
  while(end_loop_early == false && (now() - start_time) < max_processing_seconds*1000)
  {
    loop_counter++
    debug.push("LOOP " + loop_counter )
    //Pull 100 records.
    var n = 100
    var table = 'x_fmcna_armis_inte_armis_device_table'
    var other_params = {force_field: 'novel_link_attempted',force_pending: false}

    //See documentation for how this works.
    var end_sys_id = getPendingRecordsEndSysID(n,table,other_params)
    debug.push("End sys_id (for record pull): " + end_sys_id)

    var rec1 = getNumPendingRecords(n,table,end_sys_id,other_params)
    var rec2 = getNumPendingRecords(n,table,end_sys_id,other_params)

    while(rec1.next())
    {

      armis_attempts_count++
      var result = attemptGoldenMatch(rec1)
      debug.push(result.debug)

      if (result.match == false)
      {
        result = attemptMACMatch(rec1)
        debug.push(result.debug)
      }

      //By now we have exhausted all opportunities for matching.
      //If we have a match, store it, otherwise we're done.
      if(result.match == true)
      {
        armis_successes_count++
        rec1.setValue('linked_snow_computer',result.matched_device_sys_id)
        rec1.update()
        debug.push("Assigned matching SNOW computer. \n")
      }
      else { debug.push("No match found.\n") }
    }

    //All updates have been completed, set attempted flags.
    rec2.setValue('novel_link_attempted',true)
    rec2.updateMultiple()
  }

  outputs.debug = debug.join('\n')
  var report_string = "Attempted " + armis_attempts_count + " armis-to-snow links, " + armis_successes_count + " successful."
  writeToActionLog({id: generateID(), action_type: 'LINK_AttemptAllArmisLinks', total_time: (now() - start_time), api_time: 0, heavy_processing_time: (now() - start_time), report: report_string, debug_string: outputs.debug})

  outputs.debug = debug.join('\n')
})(inputs, outputs);

