//Param template: queueApplication({execution_id: , armis_id: , name: , version: })
//Create an ARP entry. This must be sent along with updates (for reasons Armis doesn't advise me, they just say they need it).
function queueApplication(p) 
{
  if (p.armis_id == null || p.armis_id == "") { return {operation: "error: Armis ID cannot be empty", time_delta: 0} }
  if (p.name == null || p.name == "") { return {operation: "error: Software name cannot be empty", time_delta: 0} }
  //Attributes must fall within a set of attributes allowed by Armis. (GET /api/v1/devices/csv/)
  var q = {}
  q.table = 'x_fmcna_armis_inte_armis_op_application_upload'
  q.unique_pairs = [['armis_device_id',p.armis_id],['application_name',p.name]]
  q.update_pairs = [['execution_id',p.execution_id],['application_version',p.version],['status','PENDING']]
  return insertOrUpdate(q)
}

(function execute(inputs, outputs) {
  var debug = []

  inputs.record = new GlideRecord('cmdb_software_instance')
  inputs.record.addQuery('sys_id','a83fd02cdb86045490d8d0b2ca9619aa')
  inputs.record.query()
  inputs.record.next()
  outputs.debug = inputs.record.getRowCount()

  if (inputs.record == null) { return }
  if (inputs.record.sys_id == null) { return }
  outputs.debug = 'Installed on: ' + inputs.installed_on
  if (inputs.installed_on == null) { return }

  //Immediately discard if it doesn't have a valid name.
  var software = inputs.record.software
  if (software == null) { return }

  var sw_name = '' + software.name
  if (sw_name == '') { return }
  debug.push('Software name: ' + sw_name)

  //Next, is this on our Armis Device table? If not, we don't care to respond.
  var q = new GlideRecord('x_fmcna_armis_inte_armis_device_table')
  q.addQuery('linked_snow_computer',inputs.installed_on)
  q.query()

  if(!q.next()) { return }

  //It has a name, and has an associated ID. Let's do it.
  queueApplication({execution_id: 'event', armis_id: q.armis_device_id, name: sw_name, version: '' + software.version})

  ouputs.debug = debug.join('\n')
})(inputs, outputs);

