sys_variable_value.value:
(function execute(inputs, outputs) {

  //CATEGORY--we have a small whitelist of available categories. This is populated semi-manually.
  var armis_category = ""
  if (inputs.category == "Hardware" & inputs.subcategory == "Computer") { armis_category = "Computer" }

  //This enforces RFC4180 CSV encapsulation. (Section 2.5 - 2.7)
  //  the string '"ad""df' becomes '"""ad""""df"'
  function csv_encapsulate(s,force_encapsulate)
  {
    if (s==null) { return "" }
    if (force_encapsulate == true || s.includes('"') || s.includes(',') || s.includes('\n')) { s = '"' + s.split('"').join('""') + '"' }
    return s;
  }

  //A CSV line (per Jose from Armis) is formatted as follows:
  // - 1st field is device ID if known, 0 otherwise
  // - 2nd field is MAC (required)
  // - 3rd field is the field to modify, from the following list:
  //       CATEGORY
  //       IP
  //       LAST_SEEN
  //       MODEL
  //       NAME
  //       OS
  //       OS_VERSION
  //       TAG
  //       TYPE
  //       USER
  // - 4th field is the value.

  //quick reference to the line starter
  var device_id = inputs.armis_device_id
  if (inputs.armis_device_id == null) { device_id = "0" }
  var line_starter = "" + device_id + "," + inputs.armis_mac_address + ","

  //Build the CSV!
  var csv = "id,mac,key,value\r\n"

  function csv_line(line_starter,val,tag,force_encapsulate)
  {
    if (val != null & val != "") { return line_starter + tag + "," + csv_encapsulate(val,force_encapsulate) + "\r\n" }
    return ""
  }

  csv += csv_line(line_starter,inputs.category,"CATEGORY",false)
  csv += csv_line(line_starter,inputs.ip_address,"IP",false)
  csv += csv_line(line_starter,inputs.name,"NAME",true)
  //csv += csv_line(line_starter,inputs.last_seen.toString(),"LAST_SEEN",false)
  csv += csv_line(line_starter,inputs.model,"MODEL",false)
  csv += csv_line(line_starter,inputs.os,"OS",false)
  csv += csv_line(line_starter,inputs.os_version,"OS_VERSION",false)
  csv += csv_line(line_starter,inputs.type,"TYPE",false)
  csv += csv_line(line_starter,inputs.user,"USER",false)
  csv += csv_line(line_starter,inputs.universal_tag,"TAG",true)

  //Loop through tags
  if (inputs.tags != null)
  {
    var i = 0;
    for (i=0;i<inputs.tags.length;i++)
    {
      csv += csv_line(line_starter,inputs.tags[i],"TAG")
    }
  }

  outputs.csv = csv


})(inputs, outputs);

