sys_variable_value.value:
(function execute(inputs, outputs) {
  var num_records = parseInt(inputs.num_records);
  var now = new GlideDateTime();

  outputs.report = "Time to iterate through " + inputs.num_records + " records: " + GlideDateTime.subtract(inputs.run_start_time,now).getNumericValue()/1000 + "s\n";

  //Records per minute:
  // num_records * 60 / (current time - start time) 
  var avg = inputs.num_records * 60 / ( GlideDateTime.subtract(inputs.run_start_time,now).getNumericValue()/1000 )
  outputs.report += "Average time per record: " + avg + " rec/min";

})(inputs, outputs);

