sys_variable_value.value:
  /*

  This script repeatedly queries Armis' "GET Devices" endpoint and records its entire database of devices to the Armis Device Table.

  It starts from record N, where N is  "getd_[mode]_current_n" on the cache table.

  Up to 100 records can be queried at a time, so it loops as long as it is allowed (specified by max_runtime_seconds). 

  The query returns with a "next" value and a "total" value that are recorded to the cache table. "next" is null if the end of the list has been reached.

  The script has two modes: "master" and "refresh".
  - "master" mode never resets. Its goal is to always keep the table updated with brand new devices, so it's always querying for the latest devices it can find. It should query for new devices every 5 minutes.
  - "refresh" mode's goal is to go through the entire Armis database and refresh the properties of all active devices. This should be reset on a cadence (once a day/week/etc.)

  */

//GENERAL FUNCTION LIBRARY v0.9 MINIFIED
//SEE FUNCTION_LIBRARY ACTION FOR DOCUMENTATION AND FULL VERSION
function now(){return(new GlideDateTime).getNumericValue()}function lapReport(e){var r="("+(now()-e)+"ms)";return e=now(),r}function includes(e,r){for(var t=0;t<e.length;t++)if(e[t]==r)return!0;return!1}function digest(e){for(var r=5381,t=e.length;t;)r=33*r^e.charCodeAt(--t);return r>>>0}function isValidIPAddress(e){return!!RegExp("^([01]?[0-9]?[0-9]|2[0-4][0-9]|25[0-5])\\.([01]?[0-9]?[0-9]|2[0-4][0-9]|25[0-5])\\.([01]?[0-9]?[0-9]|2[0-4][0-9]|25[0-5])\\.([01]?[0-9]?[0-9]|2[0-4][0-9]|25[0-5])$").test(e)||!!RegExp("^((?:[0-9A-Fa-f]{1,4}))((?::[0-9A-Fa-f]{1,4}))*::((?:[0-9A-Fa-f]{1,4}))((?::[0-9A-Fa-f]{1,4}))*|((?:[0-9A-Fa-f]{1,4}))((?::[0-9A-Fa-f]{1,4})){7}$").test(e)}function isValidMACAddress(e){return RegExp("^([0-9A-Fa-f]{2}[:-]){5}([0-9A-Fa-f]{2})|([0-9a-fA-F]{4}\\.[0-9a-fA-F]{4}\\.[0-9a-fA-F]{4})$").test(e)}function generateID(){var e="";arr="0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ";for(var r=6;r>0;r--)e+=arr[Math.floor(Math.random()*arr.length)];return e}function insertOrUpdate(e){var r={},t=now();null==e.unique_pairs&&(e.unique_pairs=[]);try{var a=new GlideRecord(e.table);if(0!=e.unique_pairs.length){for(var i=0;i<e.unique_pairs.length;i++)a.addQuery(e.unique_pairs[i][0],e.unique_pairs[i][1]);a.query()}if(0==a.getRowCount()){a.initialize();for(i=0;i<e.unique_pairs.length;i++)a[e.unique_pairs[i][0]]=e.unique_pairs[i][1];for(i=0;i<e.update_pairs.length;i++)a[e.update_pairs[i][0]]=e.update_pairs[i][1];a.insert(),r.operation="insert"}else{for(i=0;i<e.update_pairs.length;i++)a[e.update_pairs[i][0]]=e.update_pairs[i][1];a.updateMultiple(),r.operation="update"}}catch(e){r.operation="error: "+e}return r.time_delta=now()-t,r}function writeToActionLog(e){var r={table:"x_fmcna_armis_inte_armis_action_log"};return r.unique_pairs=[["execution_id",e.id]],r.update_pairs=[["action_type",e.action_type],["api_time",e.api_time],["heavy_processing_time",e.heavy_processing_time],["total_time",e.total_time],["report",(""+e.report).substring(0,199)],["debug_string",(""+e.debug_string).substring(0,1999)]],insertOrUpdate(r)}function writeToErrorLog(e){var r={table:"x_fmcna_armis_inte_armis_error_log"};return r.unique_pairs=[["execution_id",e.id]],r.update_pairs=[["error_message",e.error_message],["debug_string",e.debug_string]],insertOrUpdate(r)}function getConfigValue(e){var r=new GlideRecord("x_fmcna_armis_inte_armis_integration_configuration");return r.addQuery("key",e),r.query(),0==r.getRowCount()?"":(r.next(),r.getValue("value"))}function getCacheValue(e,r){var t=new GlideRecord("x_fmcna_armis_inte_armis_integration_cache");if(t.addQuery("key",e),t.query(),0==t.getRowCount())return null;switch(t.next(),r){case"str":return t.getValue("value_string");case"int":return t.getValue("value_int");case"datetime":return t.getValue("value_datetime")}}function getCurrentToken(){return getCacheValue("token","str")}function setCacheValue(e,r,t){var a={table:"x_fmcna_armis_inte_armis_integration_cache"};switch(a.unique_pairs=[["key",e]],t){case"str":a.update_pairs=[["value_string",r]];break;case"int":a.update_pairs=[["value_int",r]];break;case"datetime":a.update_pairs=[["value_datetime",r]]}return insertOrUpdate(a)}function deleteCacheValue(e){var r=new GlideRecord("x_fmcna_armis_inte_armis_integration_cache");r.addQuery("key",e),r.query(),r.deleteMultiple()}



//UPDATE ARMIS DEVICE (v0.2)
//MINIFIED -- FIND THE FULL VERSION IN THE FUNCTION_LIBRARY ACTION
function updateArmisDevice(e,i){var s=(new GlideDateTime).getNumericValue(),a={},t=new GlideRecord("x_fmcna_armis_inte_armis_device_table"),r=digest(JSON.stringify(e));a.digest=r,t.addQuery("armis_device_id",e.id),t.query();var n=0==t.getRowCount();n?t.initialize():t.next(),a.is_insert=n;var d,_=!0;if(n||t.getValue("digest")!=r||(_=!1),a.perform=_,a.digest_existing=t.getValue("digest"),_){if(t.access_switch=e.accessSwitch,t.armis_device_id=e.id,t.armis_risk_level=e.riskLevel,t.category=e.category,t.first_seen=new GlideDateTime(e.firstSeen),t.ip_address=e.ipAddress,t.last_seen=new GlideDateTime(e.lastSeen),t.mac_address=e.macAddress,t.manufacturer=e.manufacturer,t.model=e.model,t.name=e.name,t.os=e.operatingSystem,t.os_version=e.operatingSystemVersion,null!=e.sensor&&(t.sensor_name=e.sensor.name),null!=e.sensor&&(t.sensor_type=e.sensor.type),null!=e.site&&(t.site=e.site.name),null!=e.site&&(t.site_location=e.site.location),t.armis_tags=JSON.stringify(e.tags),t.visibility=e.visibility,t.digest=r,n?(d=t.insert(),a.action="insert"):(d=t.update(),a.action="update"),a.record=d,i){null==e.tags&&(e.tags=[]);var l=new GlideRecord("x_fmcna_armis_inte_armis_device_tags");l.addQuery("armis_device",d),l.query();for(var m=[];l.next();){var c=l.getValue("armis_tag");includes(m,l.getValue("armis_tag"))?m.push(c):l.deleteRecord()}for(var o=0;o<e.tags.length;o++)if(!includes(m,e.tags[o])){var g=new GlideRecord("x_fmcna_armis_inte_armis_device_tags");g.initialize(),g.armis_device=d,g.armis_tag=e.tags[o],g.insert()}}}else a.action="no_update_needed";return a.time_delta=(new GlideDateTime).getNumericValue()-s,a}

// *********************************
// *** ACTION-SPECIFIC FUNCTIONS ***
// *********************************

function calculateTimeLeft(start_time,max_runtime_seconds)
{
 var elapsed_ms = now() - start_time
 return max_runtime_seconds - elapsed_ms/1000
}

function writeToAPILog(p)
{
  if (p.payload == null) { p.payload = "" }
  if (p.response_body == null) { p.response_body = "" }
  var q = {}
  q.table = 'x_fmcna_armis_inte_armis_rest_api_log'
  q.update_pairs = [['execution_id',p.id],['api_endpoint',p.endpoint],['payload',('' + p.payload).substring(0,1999)],['response_code',p.response_code],
    ['response_body',('' + p.response_body).substring(0,1999)],['query_submitted_at',p.start_time],['query_response_time_ms',p.time_delta_ms]]
  return insertOrUpdate(q)
}


// **********************
// *** IMPLEMENTATION ***
// **********************


(function execute(inputs, outputs) {

  //Escape early
  if(inputs.mode == 'test') { return }
  if(inputs.max_runtime_seconds <= 0) { outputs.time_left_s = inputs.max_runtime_seconds; return }

  //Init
  var execution_id = generateID()
  var debug = []
  var start_time = now()
  var api_total = 0
  var heavy_total = 0
  var lap = now()
  var num_api_calls = 0
  var num_armis_devices = 0
  var num_records_updated = 0
  var num_records_inserted = 0
  var num_records_skipped = 0
  var total_mode_records = 0
  var error_report = '';
  var report = '';
  var original_getd_n = -1
  outputs.time_left_s = 0 // just in case.
  var num_refresh_threshold = 20 //The number of devices to go back compared to the "next" value, in order to ensure that we get some degree of overlap and catch missed devices

  //Set up query
  var armis_query = getConfigValue('armis_' + inputs.mode + '_query')  
  var cache_key = "getd_" + inputs.mode + "_current_n"

  debug.push("QUERY= '" + armis_query + "'." + lapReport(lap));lap=now()

  //Ending counters/report

  try {

    //LOOP - until we are out of time.
    // [current time] - [start_time] is in milliseconds, divide to get seconds elapsed.
    var end_loop_early = false // Is flagged manually if the query fails outright
    var max_runtime_seconds = parseInt(inputs.max_runtime_seconds)
    if(isNaN(max_runtime_seconds)) { max_runtime_seconds = 240 }
    while(end_loop_early == false && (now() - start_time) < max_runtime_seconds*1000)
    {
      debug.push("LOOP " + num_api_calls + " BEGIN "+ lapReport(lap));lap=now()

      //GET CURRENT ITERATION'S START PAGE (getd_n). This is in the cache table. If it doesn't exist, start at n=0
      var getd_n = parseInt(getCacheValue(cache_key,'int'))
      if (isNaN(getd_n)) { getd_n = 0 }
      debug.push("getd_n: " + getd_n + " " + lapReport(lap));lap=now()
      if(original_getd_n == -1) { original_getd_n = getd_n} // used for reporting.

      //We want 100 devices, starting at device N
      //Using Armis API endpoint GET /api/v1/search/
      var request = new sn_ws.RESTMessageV2('Armis API','GET Search')
      request.setRequestHeader('Authorization',getCurrentToken())
      request.setQueryParameter('aql',armis_query)
      request.setQueryParameter('from',getd_n)
      request.setQueryParameter('length',100)
      request.setQueryParameter('orderBy','id')

      //Execute query, but sandwich .execute() with a time check to document how long it took.
      var glide_start_time = new GlideDateTime()
      var t = now()
      var response = request.execute()
      api_total += now() - t

      debug.push("Queried 100 devices, response: " + response.getStatusCode() + " " + lapReport(lap));lap=now()
      if (response.getStatusCode() != 200) { debug.push(response.getBody()) }

      //Log the query results
      writeToAPILog({id: execution_id, endpoint: 'get_search', payload: armis_query, response_code: response.getStatusCode(), 
                     response_body: response.getBody(), start_time: glide_start_time, time_delta_ms: (now() - t)})
      debug.push("Logged results to Armis API Log. "  + lapReport(lap));lap=now()
      num_api_calls += 1

      //Extract data!
      if(response.getStatusCode() == 200)
      {
        //Parse!
        var data = JSON.parse(response.getBody()).data
        total_mode_records = data.total
        debug.push("Parsed data. count=" + data.count + ", next=" + data.next + ", total=" + data.total + " "  + lapReport(lap));lap=now()

        num_armis_devices += data.count
        if(inputs.mode == 'refresh') { num_armis_devices -= num_refresh_threshold } //we want to be able to accurately track that we're backing up each step by X records.

        //Note that if we're at the end of the list of devices, next will be null.
        //What happens next depends on the mode:
        // - if mode==master, we want to just keep the current "next" at the total so that when we check again, we're checking for NEW devices.
        // - if mode==refresh, we want to loop back around to 0 and start again when we've finished the refresh stack.
        if (data.next == null) {
          switch (inputs.mode) {
            case 'master':
              setCacheValue(cache_key, data.total, 'int')
              break;
            case 'refresh':
              setCacheValue(cache_key, 0, 'int');
              report += '[MAXED,RESET TO 0] '
              break;
          }
        }
        else { 
          //Update the cache so the next run starts where we left off.
          //Note that for refresh, we want to go a few steps backward (num_refresh_threshold) so that we can try to catch any new devices that may have been added since we last checked.
          //This won't be perfect but should go a long way toward improving consistency.
          switch (inputs.mode) {
            case 'master': setCacheValue(cache_key, data.next, 'int');break
            case 'refresh': setCacheValue(cache_key, data.next - num_refresh_threshold, 'int');break
          }
        }

        //Set the total too, just for reference.
        setCacheValue("getd_" + inputs.mode + "_total", data.total, 'int')

        //Next, loop through all 100-ish returns. First, we expect this will be heavy processing, so let's mark it out separately.
        var t = now()

        if (data.count > 0) {
          for (var i=0;i<data.count;i++)
          {
            //Alllllll the fields in the query need to be pushed to the Armis Device Table.
            var entry = data.results[i]

            //I've abstracted this functionality into the function library since I'll be using it in more than one action, see FUNCTION_LIBRARY documentation
            var results = updateArmisDevice(entry,inputs.tags_to_table)
            debug.push(results.action + ":" + entry.id + " (" + entry.name + "). " + lapReport(lap));lap=now()
            switch (results.action) {
              case 'insert': num_records_inserted += 1; break
              case 'update': num_records_updated += 1; break
              case 'no_update_needed': num_records_skipped += 1; break
            }
          }
        }
        else
        {
          //We have 0 records.
          //If this is our first query in MASTER mode, that means we're maxed out. We don't even want to log anything, just drop the script.

          if (num_api_calls == 1 && inputs.mode == 'master') {
            outputs.debug = "0 records, killed script."
            outputs.time_left_s = calculateTimeLeft(start_time,max_runtime_seconds)
            return
          }
          //If we're in master mode, we have processed some records so we still want to report, but we're done looping.
          //(We do want to set a flag in the cache that the master is maxed out, so triggers can start processing.)
          if (inputs.mode == 'master')
          {
            setCacheValue('master_complete', 1, 'int')
            end_loop_early = true
          }
          //Any other case (i.e. refresh mode), we have just reset the counter to 0 and we want to keep looping.
        }

        //Document our heavy processing time
        heavy_total += now() - t
      }
      else
      {
        //API call was not successful. Not really anything to be done, but end the loop--perhaps we had a bad token?
        report += "API failed: (" + response.getStatusCode() + ") " + response.getBody() + " "
        end_loop_early = true 
      }
    }
  }
  catch(ex)
  {
    error_report = '[ERROR: ' + ex + '] '
    writeToErrorLog({id: execution_id, error_message: ex, debug_string: debug_string.join('\n')})
  }

  //Percentage reporting, to get a good idea of how far we've come along.
  var start_pct = ( 100 * (original_getd_n) / total_mode_records ).toFixed(1)
  var done_pct = ( 100 * (parseInt(num_armis_devices + num_refresh_threshold) + parseInt(original_getd_n)) / total_mode_records ).toFixed(1)
  var percentage_insert = " (" + start_pct + "%->" + done_pct + "%)"

  //Build and store the action's results to the action log.
  report += "[" + inputs.mode.toUpperCase() + "]" + percentage_insert + " From n=" + original_getd_n + "/" + total_mode_records + ", made " + num_api_calls + " API calls, total " + num_armis_devices + " devices (inserted:" + num_records_inserted + ", updated:" + num_records_updated + ", skipped:" + num_records_skipped + "). Time left:" + Math.round(calculateTimeLeft(start_time,max_runtime_seconds)) + "s."
  debug = debug.join('\n')

  writeToActionLog(
    {id: execution_id, action_type: 'GETD_GetBulkArmisData', total_time: (now() - start_time), api_time: api_total, 
     heavy_processing_time: heavy_total, report: report, debug_string: debug})

  outputs.debug = debug
  outputs.time_left_s = calculateTimeLeft(start_time,max_runtime_seconds)
})(inputs, outputs);

