sys_variable_value.value:
(function execute(inputs, outputs) {
  // We have N records, in reverse order by sys_id
  // The first record in this pull is the Nth record in the table.
  // So its sys_id is the one we want.
  // We also want the LAST record, so we need to do next() for the count() number of times. (the first next() pulls the first record)
  // getUniqueValue gives us the sys_id.
  var i = 0;
  for (i=0;i<inputs.count;i++) { inputs.records.next() }
  outputs.nth_sys_id = inputs.records.sys_id
})(inputs, outputs);

