sys_variable_value.value:
//As yet, haven't figured out how to make these dynamically map without doing a JSON.parse() for every item in the Create/Update, so this will have to do.
//Goes through all elements of the JSON return from Armis and sets output variables, which are then written to the table.

(function execute(inputs, outputs) {
var json = JSON.parse(inputs.json_string)
outputs.json_object = json
outputs.access_switch = json.accessSwitch
outputs.category = json.category
outputs.first_seen = new GlideDateTime(json.firstSeen)
outputs.id = json.id
outputs.ip_address = json.ipAddress
outputs.last_seen = new GlideDateTime(json.lastSeen)
outputs.mac_address = json.macAddress
outputs.manufacturer = json.manufacturer
outputs.model = json.model
outputs.name = json.name
outputs.operating_system = json.operatingSystem
outputs.operating_system_version = json.operatingSystemVersion
outputs.risk_level = json.riskLevel
outputs.sensor_name = json.sensor.name
outputs.sensor_type = json.sensor.type
outputs.site_name = json.site.name
outputs.site_location = json.site.location
outputs.type = json.type
outputs.user = json.user
outputs.visibility = json.visibility

var tags = []
if(json.tags.length > 0)
{
  for (var i=0;i<json.tags.length;i++)
  {
    tags[i] = json.tags[i]

    //This part creates a new entry in the choices list. Borrowed from:
    // https://community.servicenow.com/community?id=community_question&sys_id=51eec3a1dbdcdbc01dcaf3231f9619d3
    var gr = new GlideRecord('sys_choice')
    gr.addQuery('name',"x_fmcna_armis_inte_armis_tags")
    gr.addQuery('element',"tag")
    gr.addQuery('value',tags[i])
    gr.query()

    if (!gr.next())
    {
      var gr1 = new GlideRecord('sys_choice')
      gr1.initialize()
      gr1.name = "x_fmcna_armis_inte_armis_tags"
      gr1.element = "tag"
      gr1.value = tags[i]
      gr1.label = tags[i]
      gr1.language = "en"
      gr1.insert()
    }
  }
}
outputs.tags = tags
outputs.tags_count = json.tags.length


  //This is set for now, see "script tags"--currently disabled
outputs.tags_json = JSON.stringify(json.tags)

})(inputs, outputs);

