function now(){return(new GlideDateTime).getNumericValue()}function lapReport(e){var r="("+(now()-e)+"ms)";return e=now(),r}function includes(e,r){for(var t=0;t<e.length;t++)if(e[t]==r)return!0;return!1}function digest(e){for(var r=5381,t=e.length;t;)r=33*r^e.charCodeAt(--t);return r>>>0}function isValidIPAddress(e){return!!RegExp("^([01]?[0-9]?[0-9]|2[0-4][0-9]|25[0-5])\\.([01]?[0-9]?[0-9]|2[0-4][0-9]|25[0-5])\\.([01]?[0-9]?[0-9]|2[0-4][0-9]|25[0-5])\\.([01]?[0-9]?[0-9]|2[0-4][0-9]|25[0-5])$").test(e)||!!RegExp("^((?:[0-9A-Fa-f]{1,4}))((?::[0-9A-Fa-f]{1,4}))*::((?:[0-9A-Fa-f]{1,4}))((?::[0-9A-Fa-f]{1,4}))*|((?:[0-9A-Fa-f]{1,4}))((?::[0-9A-Fa-f]{1,4})){7}$").test(e)}function isValidMACAddress(e){return RegExp("^([0-9A-Fa-f]{2}[:-]){5}([0-9A-Fa-f]{2})|([0-9a-fA-F]{4}\\.[0-9a-fA-F]{4}\\.[0-9a-fA-F]{4})$").test(e)}function generateID(){var e="";arr="0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ";for(var r=6;r>0;r--)e+=arr[Math.floor(Math.random()*arr.length)];return e}function insertOrUpdate(e){var r={},t=now();null==e.unique_pairs&&(e.unique_pairs=[]);try{var a=new GlideRecord(e.table);if(0!=e.unique_pairs.length){for(var i=0;i<e.unique_pairs.length;i++)a.addQuery(e.unique_pairs[i][0],e.unique_pairs[i][1]);a.query()}if(0==a.getRowCount()){a.initialize();for(i=0;i<e.unique_pairs.length;i++)a[e.unique_pairs[i][0]]=e.unique_pairs[i][1];for(i=0;i<e.update_pairs.length;i++)a[e.update_pairs[i][0]]=e.update_pairs[i][1];a.insert(),r.operation="insert"}else{for(i=0;i<e.update_pairs.length;i++)a[e.update_pairs[i][0]]=e.update_pairs[i][1];a.updateMultiple(),r.operation="update"}}catch(e){r.operation="error: "+e}return r.time_delta=now()-t,r}function writeToActionLog(e){var r={table:"x_fmcna_armis_inte_armis_action_log"};return r.unique_pairs=[["execution_id",e.id]],r.update_pairs=[["action_type",e.action_type],["api_time",e.api_time],["heavy_processing_time",e.heavy_processing_time],["total_time",e.total_time],["report",(""+e.report).substring(0,199)],["debug_string",(""+e.debug_string).substring(0,1999)]],insertOrUpdate(r)}function writeToErrorLog(e){var r={table:"x_fmcna_armis_inte_armis_error_log"};return r.unique_pairs=[["execution_id",e.id]],r.update_pairs=[["error_message",e.error_message],["debug_string",e.debug_string]],insertOrUpdate(r)}function getConfigValue(e){var r=new GlideRecord("x_fmcna_armis_inte_armis_integration_configuration");return r.addQuery("key",e),r.query(),0==r.getRowCount()?"":(r.next(),r.getValue("value"))}function getCacheValue(e,r){var t=new GlideRecord("x_fmcna_armis_inte_armis_integration_cache");if(t.addQuery("key",e),t.query(),0==t.getRowCount())return null;switch(t.next(),r){case"str":return t.getValue("value_string");case"int":return t.getValue("value_int");case"datetime":return t.getValue("value_datetime")}}function getCurrentToken(){return getCacheValue("token","str")}function setCacheValue(e,r,t){var a={table:"x_fmcna_armis_inte_armis_integration_cache"};switch(a.unique_pairs=[["key",e]],t){case"str":a.update_pairs=[["value_string",r]];break;case"int":a.update_pairs=[["value_int",r]];break;case"datetime":a.update_pairs=[["value_datetime",r]]}return insertOrUpdate(a)}function deleteCacheValue(e){var r=new GlideRecord("x_fmcna_armis_inte_armis_integration_cache");r.addQuery("key",e),r.query(),r.deleteMultiple()}



//SET TABLES HERE


(function execute(inputs, outputs) {

  var debug = []
  var total_start_time = now()
  var lap = now()
  var total_record_deletions = 0
  var heavy_total = 0
  var error_string = ''
  var execution_id = generateID()

  var tables_to_clean = inputs.tables_to_clean

  debug.push("Tables to clean (from MAIN input): " + JSON.stringify(tables_to_clean) + lapReport(lap));lap=now()

  try
  {
    var tables_length = 0
    if(tables_to_clean != null) { tables_length = tables_to_clean.length }
    for(i=0;i<tables_length;i++)
    {
      var table_name = tables_to_clean[i].table_name
      var num_hours_old = tables_to_clean[i].num_hours_old
      debug.push("Cleaning up '" + table_name + "' (records older than '" + num_hours_old + "') "  + lapReport(lap));lap=now()

      var cleanup_time = new GlideDateTime()
      cleanup_time.addSeconds( -1 * 3600 * num_hours_old)
      debug.push("Cleanup cutoff: " + cleanup_time.getDisplayValue())

      var q = new GlideRecord(table_name)
      q.addQuery('sys_updated_on','<',cleanup_time)

      var t = now()
      q.query()
      heavy_total += now() - t

      var num_records = q.getRowCount()
      var start_time = now()
      if (num_records > 0)
      {
        var t = now()
        q.deleteMultiple()
        heavy_total += now() - t

        total_record_deletions += num_records
        debug.push("    " + num_records + " records deleted. "  + lapReport(lap));lap=now()
      }
      else
      {
        debug.push("    No records to delete. "  + lapReport(lap));lap=now()
      }
    }

    //Next, populate the cache with a list of fields we are interested in during updates.

    var q = new GlideRecord('x_fmcna_armis_inte_armis_snow_attribute_map')
    q.query()
    var list = []
    while(q.next())
    {
      list.push(q.getValue('snow_field'))
    }
    setCacheValue('snow_field_list',list.join(','),'str')
    debug.push("Populating SNOW field list: " + list.join(',')  + lapReport(lap));lap=now()

  }
  catch(ex)
  {
    error_string = '[ERROR: ' + ex + '] '
    debug.push(error_string + lapReport(lap));lap=now()
    writeToErrorLog({id: execution_id, error_message: ex, debug_string: debug.join('\n')})
  }

  debug = debug.join('\n')
  var report = "Cleaned up " + total_record_deletions + " records."
  writeToActionLog({id: execution_id, action_type: "CLEANUP_AllTables", total_time: now()-total_start_time, api_time: 0, heavy_processing_time: heavy_total, report: report, debug_string: debug})
  outputs.debug = debug
})(inputs, outputs);

