ery("key",e),i.query(),0==i.getRowCount()?"":(i.next(),i.getValue("value"))}function getCacheValue(e,i){var t=new GlideRecord("x_fmcna_armis_inte_armis_integration_cache");if(t.addQuery("key",e),t.query(),0==t.getRowCount())return null;switch(t.next(),i){case"str":return t.getValue("value_string");case"int":return t.getValue("value_int");case"datetime":return t.getValue("value_datetime")}}function getCurrentToken(){return getCacheValue("token","str")}function setCacheValue(e,i,t){var a={table:"x_fmcna_armis_inte_armis_integration_cache"};switch(a.unique_pairs=[["key",e]],t){case"str":a.update_pairs=[["value_string",i]];break;case"int":a.update_pairs=[["value_int",i]];break;case"datetime":a.update_pairs=[["value_datetime",i]]}return insertOrUpdate(a)}function deleteCacheValue(e){var i=new GlideRecord("x_fmcna_armis_inte_armis_integration_cache");i.addQuery("key",e),i.query(),i.deleteMultiple()}function updateArmisDevice(e,i){var t=(new GlideDateTime).getNumericValue(),a={},r=new GlideRecord("x_fmcna_armis_inte_armis_device_table"),n=digest(JSON.stringify(e));a.digest=n,r.addQuery("armis_device_id",e.id),r.query();var s=0==r.getRowCount();s?r.initialize():r.next(),a.is_insert=s;var u,_=!0;if(s||r.getValue("digest")!=n||(_=!1),a.perform=_,a.digest_existing=r.getValue("digest"),_){if(r.access_switch=e.accessSwitch,r.armis_device_id=e.id,r.armis_risk_level=e.riskLevel,r.category=e.category,r.first_seen=new GlideDateTime(e.firstSeen),r.ip_address=e.ipAddress,r.last_seen=new GlideDateTime(e.lastSeen),r.mac_address=e.macAddress,r.manufacturer=e.manufacturer,r.model=e.model,r.name=e.name,r.os=e.operatingSystem,r.os_version=e.operatingSystemVersion,r.sensor_name=e.sensor.name,r.sensor_type=e.sensor.type,r.site=e.site.name,r.site_location=e.site.location,r.armis_tags=JSON.stringify(e.tags),r.visibility=e.visibility,r.digest=n,s?(u=r.insert(),a.action="insert"):(u=r.update(),a.action="update"),a.record=u,i){null==e.tags&&(e.tags=[]);var o=new GlideRecord("x_fmcna_armis_inte_armis_device_tags");o.addQuery("armis_device",u),o.query();for(var d=[];o.next();){var l=o.getValue("armis_tag");includes(d,o.getValue("armis_tag"))?d.push(l):o.deleteRecord()}for(var c=0;c<e.tags.length;c++)if(!includes(d,e.tags[c])){var p=new GlideRecord("x_fmcna_armis_inte_armis_device_tags");p.initialize(),p.armis_device=u,p.armis_tag=e.tags[c],p.insert()}}}else a.action="no_update_needed";return a.time_delta=(new GlideDateTime).getNumericValue()-t,a}



//SET TABLES HERE


(function execute(inputs, outputs) {

  var tables_to_cleanup = [
    'x_fmcna_armis_inte_armis_action_log',
    'x_fmcna_armis_inte_armis_op_arp_table',
    'x_fmcna_armis_inte_armis_op_key_attribute',
    'x_fmcna_armis_inte_armis_op_property_upload',
    'x_fmcna_armis_inte_armis_rest_api_log'
  ]

  var debug = []
  var total_start_time = now()
  var total_record_deletions = 0
  var heavy_processing_time = 0

  //Pull the number of hours-old a record should be from the config table.
  var cleanup_hours = getConfigValue('cleanup_older_than_hours')
  debug.push("cleanup_hours=" + cleanup_hours + " (the number of hours-old a record should be to get purged)")

  //Calculate the GlideDateTime() that represents the threshold for deletion
  var cleanup_time = new GlideDateTime()
  debug.push("now=" + cleanup_time + " (current time)")
  cleanup_time.addSeconds( -1 * 3600 * cleanup_hours)
  debug.push("cleanup_time=" + cleanup_time + " (records older than this time will get purged)")

  debug.push("Tables to clean up: \n    " + tables_to_cleanup.join("    \n"))

  for(i=0;i<tables_to_cleanup.length;i++)
  {
    var table_name = tables_to_cleanup[i]
    debug.push("cleaning up '" + table_name + "'")

    var q = new GlideRecord(table_name)
    q.addQuery('sys_updated_on','<',cleanup_time)

    var heavy_processing_start = now()
    q.query()

    var num_records = q.getRowCount()
    var start_time = now()
    if (num_records > 0)
    {
      q.deleteMultiple()
      total_record_deletions += num_records
      var time_delta = now() - start_time
      debug.push("    " + num_records + " records deleted. (" + time_delta + "ms.)")
    }
    else
    {
      debug.push("    No records to delete.")
    }
    heavy_processing_time += now() - heavy_processing_start
  }

  var report = "Cleaned up " + total_record_deletions + " records older than " + cleanup_hours + " hours."
  writeToActionLog({id: generateID(), action_type: "CLEANUP_AllTables", total_time: now()-total_start_time, api_time: 0, heavy_processing_time: heavy_processing_time, report: report, debug_string: debug.join('\n')})
  outputs.debug = debug.join('\n')
})(inputs, outputs);

