  var csv = ["ip,mac"]
  while(r.next()) { csv.push(r.getValue('ip') + "," + r.getValue('mac')); r.setValue('status','COMPLETE'); r.update() }
  debug.push("Iterated through multiple. (" + (now() - last_t) + "," + r.getRowCount() + ")")
